    public static boolean existTipo(String tipo){
        return Arrays.asList(tipos).contains(tipo);
    }
    
    public static boolean existSubtipo(String subTipo){
        return Arrays.asList(subTipos).contains(subTipo); 
    }
    
    public static boolean exist(String tipo, String subTipo){
        //El tipo es un *
        if(tipo.equals("*")){
            if(subTipo.equals("*")){
                return true; //cualquier tipo de cualquier subtipo
            }else{//cualquier tipo pero subtipo especifico
                System.out.println("existe: "+Arrays.asList(subTipos).contains(subTipo));
                return Arrays.asList(subTipos).contains(subTipo);
            }
        }else{//el tipo es textual
            if(subTipo.equals("*")){//el subtipo es cualquiera
                return true;
            }else{//Se tiene tipo y subtipo
                //se obtiene el indice ya que es un elemento en concreto
                int indexT=Arrays.asList(tipos).indexOf(tipo);
                int indexS=Arrays.asList(subTipos).indexOf(subTipo);
                if(indexT==-1||indexS==-1){//Si no existe alguno de los dos se retorna falso
                    return false;
                }else{//en caso de existir ambos deben tener el mismo indice
                    return indexT==indexS?true:false;
                }
            }
        }
    }
    
    public static String getMimeType(String subTipo){
        int indexS=Arrays.asList(subTipos).indexOf(subTipo);
        String sT=subTipos[indexS];
        String t=tipos[indexS];
        return t+"/"+sT;
    }
